'use strict';

var isArray = require( '@stdlib/assert/is-array' );
var UnicodeTable = require( './../lib' );

function styleGreen( value ) {
	var i;
	if ( isArray( value ) ) {
		for ( i = 0; i < value.length; i++ ) {
			value[ i ] = '\u001b[32m' + value[ i ] + '\u001b[0m';
		}
		return value;
	}
	return '\u001b[32m' + value + '\u001b[0m';
}

// Define a data set containing emoji:
var data = [
	[
		'\u001b[31mbeep\u001b[0m', // red
		'\u001b[1mboop\u001b[0m', // bold
		'\u001b[44mfoo\u001b[0m', // blue_bg
		'\u001b[4;47mbar\u001b[0m' // underline, white_bg
	]
];

// Define column headers:
var headers = [ 'A', 'B', 'C', 'D' ];

// Create a table:
var table = new UnicodeTable( data, {
	'headers': headers,
	'marginLeft': 5,
	'marginRight': 5,
	'align': 'right',
	'maxColumnWidth': 10
});

table.rowSeparator = styleGreen( table.rowSeparator );
table.columnSeparator = styleGreen( table.columnSeparator );
table.headerSeparator = styleGreen( table.headerSeparator );

table.borders = styleGreen( table.borders );
table.corners = styleGreen( table.corners );
table.joints = styleGreen( table.joints );

// Render the table:
var str = table.render();
console.log( str );
// => '...'
