'use strict';

var random = require( '@stdlib/random/base/discrete-uniform' );
var Complex128 = require( '@stdlib/complex/float64/ctor' );
var filled2dBy = require( '@stdlib/array/base/filled2d-by' );
var UnicodeTable = require( './../lib' );

function clbk( indices ) {
	if ( indices[ 1 ] === 0 ) {
		return indices[ 0 ];
	}
	return new Complex128( random( 0, 10 ), random( 0, 10 ) );
}

// Generate a random data set:
var data = filled2dBy( [ 10, 4 ], clbk );

// Define column headers:
var headers = [ 'Row', 'Col 0', 'Col 1', 'Col 2' ];

// Create a table:
var table = new UnicodeTable( data, {
	'headers': headers,
	'rowSeparator': '-'
});

// Render the table:
var str = table.render();
console.log( str );
// => '...'
