'use strict';

var UnicodeTable = require( './../lib' );

// Define a data set containing emoji:
var data = [
	[ '😀', '🙌🏼', '👩🏾‍🚀', '👑', '🐇' ],
	[ '🙏🏻', '👉', '👏', '🤙', '☝🏿' ]
];

// Define column headers:
var headers = [ '1️⃣', '2️⃣', '3️⃣', '4️⃣', '5️⃣' ];

// Create a table:
var table = new UnicodeTable( data, {
	'headers': headers,
	'rowSeparator': '-',
	'columnSeparator': '|',
	'cellWidth': 10,
	'marginLeft': 5,
	'marginRight': 5,
	'align': 'right'
});

// Render the table:
var str = table.render();
console.log( str );
// => '...'
