'use strict';

// MODULES //

var FLOAT64_MAX = require( '@stdlib/constants/float64/max' );


// MAIN //

/**
* Returns default options.
*
* @private
* @returns {Object} default options
*/
function defaults() {
	var out = {};

	// Cell alignment:
	out.alignment = 'right';

	// Border characters:
	out.borders = '─ │ ─ │';

	// Cell padding:
	out.cellPadding = 1;

	// Corner characters:
	out.corners = '┌ ┐ ┘ └';

	// Column separator character:
	out.columnSeparator = '│';

	// Header separator character:
	out.headerSeparator = '─';

	// Row separator character:
	out.rowSeparator = 'None';

	// Joint characters:
	out.joints = '┼ ┬ ┤ ┴ ├';

	// Output margins:
	out.marginX = 0;
	out.marginY = 0;

	// Maximum cell width:
	out.maxCellWidth = FLOAT64_MAX;

	// Maximum output width:
	out.maxOutputWidth = FLOAT64_MAX;

	return out;
}


// EXPORTS //

module.exports = defaults;
