'use strict';

// MODULES //

var splitGraphemeClusters = require( '@stdlib/string/split-grapheme-clusters' );
var accessors = require( '@stdlib/array/base/accessors' );


// MAIN //

/**
* Normalizes table data.
*
* @private
* @param {Collection} buffer - linear data buffer
* @returns {Collection} modified data buffer
*/
function normalize( buffer ) {
	var obj;
	var get;
	var set;
	var raw;
	var ch;
	var v;
	var i;

	obj = accessors( buffer );
	get = obj.accessors[ 0 ];
	set = obj.accessors[ 1 ];
	for ( i = 0; i < buffer.length; i++ ) {
		raw = get( buffer, i );
		v = String( raw );
		ch = splitGraphemeClusters( v );
		set( buffer, i, {
			'raw': raw,
			'value': v,
			'graphemes': ch,
			'ngraphemes': ch.length
		});
	}
	return buffer;
}


// EXPORTS //

module.exports = normalize;
