/* eslint-disable no-invalid-this */

'use strict';

// MODULES //

var isBoolean = require( '@stdlib/assert/is-boolean' ).isPrimitive;
var format = require( '@stdlib/string/format' );


// MAIN //

/**
* Sets the rendering mode.
*
* @private
* @param {boolean} value - boolean indicating whether to automatically re-render on a `change` event
* @throws {TypeError} must be a boolean
*/
function set( value ) {
	if ( !isBoolean( value ) ) {
		throw new TypeError( format( 'invalid assignment. `%s` must be a boolean. Value: `%s`.', 'autoRender', value ) );
	}
	if ( value !== this._autoRender ) {
		this._autoRender = value;
		if ( !this._quiet ) {
			this.emit( 'change' );
		}
	}
}


// EXPORTS //

module.exports = set;
