/* eslint-disable no-invalid-this */

'use strict';

// MODULES //

var isString = require( '@stdlib/assert/is-string' ).isPrimitive;
var splitGraphemeClusters = require( '@stdlib/string/split-grapheme-clusters' );
var format = require( '@stdlib/string/format' );


// MAIN //

/**
* Sets the table left border character(s).
*
* @private
* @param {string} value - border character(s)
* @throws {TypeError} must be a string
*/
function set( value ) {
	var ch;
	if ( !isString( value ) ) {
		throw new TypeError( format( 'invalid assignment. `%s` must be a string. Value: `%s`.', 'borderLeft', value ) );
	}
	if ( value !== this._borders[ 3 ].value ) {
		ch = splitGraphemeClusters( value );
		this._borders[ 3 ] = {
			'value': value,
			'graphemes': ch,
			'length': ch.length
		};
		if ( !this._quiet ) {
			this.emit( 'change' );
		}
	}
}


// EXPORTS //

module.exports = set;
