/* eslint-disable no-invalid-this */

'use strict';

// MODULES //

var isPositiveInteger = require( '@stdlib/assert/is-positive-integer' ).isPrimitive;
var isNull = require( '@stdlib/assert/is-null' );
var FLOAT64_MAX = require( '@stdlib/constants/float64/max' );
var format = require( '@stdlib/string/format' );


// MAIN //

/**
* Sets the data buffer size.
*
* @private
* @param {(PositiveInteger|null)} value - data buffer size
* @throws {TypeError} must be a positive integer or null
* @throws {RangeError} must be greater than or equal to the number of table rows
*/
function set( value ) {
	var s;
	if ( isNull( value ) ) {
		s = FLOAT64_MAX;
	} else if ( isPositiveInteger( value ) ) {
		s = value;
	} else {
		throw new TypeError( format( 'invalid assignment. `%s` must be a positive integer or null. Value: `%s`.', 'bufferSize', value ) );
	}
	if ( this._data && s < this._data.length ) {
		throw new RangeError( format( 'invalid assignment. `%s` must be greater than or equal to the number of table rows. Number of rows: %u. Value: `%u`.', 'bufferSize', this._data.length, s ) );
	}
	if ( s !== this._bufferSize ) {
		this._bufferSize = s;
		if ( !this._quiet ) {
			this.emit( 'change' );
		}
	}
}


// EXPORTS //

module.exports = set;
