/* eslint-disable no-invalid-this */

'use strict';

// MODULES //

var isNull = require( '@stdlib/assert/is-null' );


// MAIN //

/**
* Returns the column width(s) for each table column.
*
* @private
* @returns {(Array<NonNegativeInteger|null>|NonNegativeInteger|null)} column width(s)
*/
function get() {
	if ( isNull( this._columnWidth ) ) {
		return this._columnWidth;
	}
	if ( this._columnWidth.length > 1 ) {
		return this._columnWidth.slice();
	}
	return this._columnWidth[ 0 ];
}


// EXPORTS //

module.exports = get;
