/* eslint-disable no-invalid-this */

'use strict';

// MODULES //

var pluck = require( '@stdlib/utils/pluck' ); // TODO: replace with array/base utility


// MAIN //

/**
* Returns the table corner grapheme clusters (i.e., visual characters) in the order `[top-left, top-right, bottom-right, bottom-left]`.
*
* @private
* @returns {Array<string>} corners
*/
function get() {
	return pluck( this._corners, 'value' );
}


// EXPORTS //

module.exports = get;
