/* eslint-disable no-invalid-this */

'use strict';

// MODULES //

var isNull = require( '@stdlib/assert/is-null' );
var ndarray = require( '@stdlib/ndarray/ctor' );
var pluck = require( '@stdlib/utils/pluck' );


// MAIN //

/**
* Returns table data as a read-only ndarray view.
*
* @private
* @returns {(ndarray|null)} table data
*/
function get() {
	var out;
	if ( isNull( this._data ) ) {
		return null;
	}
	out = pluck( this._data.data, 'raw' );
	return new ndarray( 'generic', out, this._data.shape, this._data.strides, this._data.offset, this._data.order );
}


// EXPORTS //

module.exports = get;
