/* eslint-disable no-invalid-this */

'use strict';

// MAIN //

/**
* Returns the alignment(s) of data within table header cells.
*
* @private
* @returns {(Array<string>|string|null)} cell alignment(s)
*/
function get() {
	if ( this._headerAlign === null ) {
		return this.align;
	}
	if ( this._headerAlign.length > 1 ) {
		return this._headerAlign.slice();
	}
	return this._headerAlign[ 0 ];
}


// EXPORTS //

module.exports = get;
