/* eslint-disable no-invalid-this */

'use strict';

// MODULES //

var isString = require( '@stdlib/assert/is-string' );
var splitGraphemeClusters = require( '@stdlib/string/split-grapheme-clusters' );
var format = require( '@stdlib/string/format' );
var wcswidth = require( './../../wcswidth.js' ); // TODO: replace with string/base/wcswidth


// MAIN //

/**
* Sets the header separator character(s).
*
* @private
* @param {string} value - header separator
* @throws {TypeError} must be a string
*/
function set( value ) {
	if ( !isString( value ) ) {
		throw new TypeError( format( 'invalid assignment. `%s` must be a string. Value: `%s`.', 'headerSeparator', value ) );
	}
	if ( value !== this._headerSeparator.value ) {
		this._headerSeparator = {
			'value': value,
			'graphemes': splitGraphemeClusters( value ),
			'columns': wcswidth( value )
		};
		if ( !this._quiet ) {
			this.emit( 'change' );
		}
	}
}


// EXPORTS //

module.exports = set;
