/* eslint-disable no-invalid-this */

'use strict';

// MODULES //

var isCollection = require( '@stdlib/assert/is-collection' );
var copy = require( '@stdlib/array/base/copy' );
var splitGraphemeClusters = require( '@stdlib/string/split-grapheme-clusters' );
var format = require( '@stdlib/string/format' );
var everyBy2 = require( './../../every_by2.js' );


// FUNCTIONS //

/**
* Predicate function which tests whether two elements are equal.
*
* @private
* @param {Object} v1 - first value
* @param {Object} v2 - second value
* @returns {boolean} boolean indicating whether two elements are equal
*/
function predicate( v1, v2 ) {
	return ( v1.value === v2.value );
}


// MAIN //

/**
* Sets the table headers.
*
* @private
* @param {Collection} values - table headers
* @throws {TypeError} must be a collection
* @throws {RangeError} must provide the correct number of columns
*/
function set( values ) {
	var data;
	var len;
	var ch;
	var v;
	var i;
	if ( !isCollection( values ) ) {
		throw new TypeError( format( 'invalid argument. `%s` must be an array-like object. Value: `%s`.', 'headers', values ) );
	}
	len = values.length;
	if ( this._numColumns ) {
		if ( len !== this._numColumns ) {
			throw new RangeError( format( 'invalid assignment. `%s` must have a length of %d in order to match the number of table columns. Value: `["%s"]`.', 'headers', this._numColumns, copy( values ).join( '", "' ) ) );
		}
	} else {
		this._numColumns = len;
	}
	data = [];
	for ( i = 0; i < len; i++ ) {
		v = String( values[ i ] );
		ch = splitGraphemeClusters( v );
		data.push({
			'value': v,
			'graphemes': ch,
			'length': ch.length
		});
	}
	if ( this._headers === null || !everyBy2( data, this._headers, predicate ) ) { // eslint-disable-line max-len
		this._headers = data;
		if ( !this._quiet ) {
			this.emit( 'change' );
		}
	}
}


// EXPORTS //

module.exports = set;
