'use strict';

// MODULES //

var isString = require( '@stdlib/assert/is-string' );
var format = require( '@stdlib/string/format' );


// VARIABLES //

var JOINT_STRING_LEN = 9;
var JOINT_CHARACTERS = 5;
var EMPTY_JOINTS = [ ' ', ' ', ' ', ' ', ' ' ];


// MAIN //

/**
* Sets the joint characters.
*
* @private
* @param {string} joints - joint character
* @throws {TypeError} must be a string with five space separated characters or 'None'
*/
function set( joints ) {
	/* eslint-disable no-invalid-this */
	var arr;

	if ( !isString( joints ) ) {
		throw new TypeError( format( 'invalid assignment. `%s` must be a string. Value: `%s`.', 'joints', joints ) );
	}
	if ( joints === 'None' ) {
		this._joints = EMPTY_JOINTS;
		return;
	}
	arr = joints.split( ' ' );
	if ( joints.length !== JOINT_STRING_LEN ) {
		throw new TypeError( format( 'invalid assignment. `%s` must be five space separated characters or \'None\'. Value: `%s`.', 'joints', joints ) );
	}
	if ( arr.length !== JOINT_CHARACTERS ) {
		throw new TypeError( format( 'invalid assignment. `%s` must be five space separated characters or \'None\'. Value: `%s`.', 'joints', joints ) );
	}
	this._joints = arr;
}


// EXPORTS //

module.exports = set;
