/* eslint-disable no-invalid-this */

'use strict';

// MODULES //

var isNonNegativeInteger = require( '@stdlib/assert/is-nonnegative-integer' ).isPrimitive;
var format = require( '@stdlib/string/format' );


// MAIN //

/**
* Sets the table's bottom margin in units of blank lines.
*
* @private
* @param {NonNegativeInteger} value - bottom margin
* @throws {TypeError} must provide a nonnegative integer
*/
function set( value ) {
	if ( !isNonNegativeInteger( value ) ) {
		throw new TypeError( format( 'invalid assignment. `%s` must be a nonnegative integer. Value: `%s`.', 'marginBottom', value ) );
	}
	if ( value !== this._margins[ 2 ] ) {
		this._margins[ 2 ] = value;
		if ( !this._quiet ) {
			this.emit( 'change' );
		}
	}
}


// EXPORTS //

module.exports = set;
