/* eslint-disable no-invalid-this */

'use strict';

// MODULES //

var isNonNegativeIntegerArray = require( '@stdlib/assert/is-nonnegative-integer-array' ).primitives;
var copy = require( '@stdlib/array/base/copy' );
var isEqualArray = require( '@stdlib/assert/is-equal-array' );
var format = require( '@stdlib/string/format' );


// VARIABLES //

var NUM_MARGINS = 4;


// MAIN //

/**
* Sets the table margins in the order `[top, right, bottom, left]`.
*
* @private
* @param {Collection<NonNegativeInteger>} values - margins
* @throws {TypeError} must be an array of nonnegative integers
* @throws {RangeError} must provide four nonnegative integers
*/
function set( values ) {
	var data;
	if ( !isNonNegativeIntegerArray( values ) ) {
		throw new TypeError( format( 'invalid assignment. `%s` must be an array of nonnegative integers. Value: `%s`.', 'margins', values ) );
	}
	data = copy( values );
	if ( data.length !== NUM_MARGINS ) {
		throw new RangeError( format( 'invalid assignment. `%s` must have a length of %d. Value: `[%s]`.', 'margins', NUM_MARGINS, data.join( ', ' ) ) );
	}
	if ( !isEqualArray( data, this._margins ) ) {
		this._margins = data;
		if ( !this._quiet ) {
			this.emit( 'change' );
		}
	}
}


// EXPORTS //

module.exports = set;
