/* eslint-disable no-invalid-this */

'use strict';

// MAIN //

/**
* Returns the maximum cell width(s) for each table column.
*
* @private
* @returns {(NonNegativeIntegerArray|NonNegativeInteger)} maximum cell width(s)
*/
function get() {
	if ( this._maxCellWidth.length > 1 ) {
		return this._maxCellWidth.slice();
	}
	return this._maxCellWidth[ 0 ];
}


// EXPORTS //

module.exports = get;
