/* eslint-disable no-invalid-this */

'use strict';

// MODULES //

var isNonNegativeInteger = require( '@stdlib/assert/is-nonnegative-integer' ).isPrimitive;
var isCollection = require( '@stdlib/assert/is-collection' );
var isNull = require( '@stdlib/assert/is-null' );
var isEqualArray = require( '@stdlib/assert/is-equal-array' );
var copy = require( '@stdlib/array/base/copy' );
var FLOAT64_MAX = require( '@stdlib/constants/float64/max' );
var format = require( '@stdlib/string/format' );


// MAIN //

/**
* Sets the maximum cell width(s) for each table column.
*
* @private
* @param {(Collection<NonNegativeInteger|null>|NonNegativeInteger|null)} value - maximum cell width(s)
* @throws {TypeError} must be a nonnegative integer, an array of nonnegative integers, or null
* @throws {RangeError} must provide the correct number of columns
*/
function set( value ) {
	var out;
	var len;
	var v;
	var i;

	if ( !isCollection( value ) ) {
		if ( isNull( value ) ) {
			out = [ FLOAT64_MAX ];
		} else if ( isNonNegativeInteger( value ) ) {
			out = [ value ];
		} else {
			throw new TypeError( format( 'invalid assignment. `%s` must be a nonnegative integer or an array of nonnegative integers. Value: `%s`.', 'maxCellWidth', value ) );
		}
		if ( !isEqualArray( out, this._maxCellWidth ) ) {
			this._maxCellWidth = out;
			if ( !this._quiet ) {
				this.emit( 'change' );
			}
		}
		return;
	}
	len = value.length;
	if ( this._numColumns ) {
		if ( len !== this._numColumns ) {
			throw new RangeError( format( 'invalid assignment. `%s` must have a length of %d in order to match the number of table columns. Value: `["%s"]`.', 'maxCellWidth', this._numColumns, copy( value ).join( '", "' ) ) );
		}
	} else {
		this._numColumns = len;
	}
	out = [];
	for ( i = 0; i < len; i++ ) {
		v = value[ i ];
		if ( isNull( v ) ) {
			out.push( FLOAT64_MAX );
			continue;
		}
		if ( isNonNegativeInteger( v ) ) {
			out.push( v );
			continue;
		}
		throw new TypeError( format( 'invalid assignment. `%s` must be a nonnegative integer or an array of nonnegative integers. Value: `["%s"]`.', 'maxCellWidth', copy( value ).join( '", "' ) ) );
	}
	if ( !isEqualArray( out, this._maxCellWidth ) ) {
		this._maxCellWidth = out;
		if ( !this._quiet ) {
			this.emit( 'change' );
		}
	}
}


// EXPORTS //

module.exports = set;
