/* eslint-disable no-invalid-this */

'use strict';

// MODULES //

var isNonNegativeInteger = require( '@stdlib/assert/is-nonnegative-integer' ).isPrimitive;
var isNull = require( '@stdlib/assert/is-null' );
var FLOAT64_MAX = require( '@stdlib/constants/float64/max' );
var format = require( '@stdlib/string/format' );


// MAIN //

/**
* Sets the maximum table width.
*
* @private
* @param {(NonNegativeInteger|null)} value - maximum width
* @throws {TypeError} must provide a nonnegative integer or null
*/
function set( value ) {
	var w;
	if ( isNull( value ) ) {
		w = FLOAT64_MAX;
	} else if ( isNonNegativeInteger( value ) ) {
		w = value;
	} else {
		throw new TypeError( format( 'invalid assignment. `%s` must be a nonnegative integer or null. Value: `%s`.', 'maxWidth', w ) );
	}
	if ( w !== this._maxWidth ) {
		this._maxWidth = w;
		if ( !this._quiet ) {
			this.emit( 'change' );
		}
	}
}


// EXPORTS //

module.exports = set;
