/* eslint-disable no-invalid-this */

'use strict';

// MODULES //

var isNonNegativeInteger = require( '@stdlib/assert/is-nonnegative-integer' ).isPrimitive;
var isNonNegativeIntegerArray = require( '@stdlib/assert/is-nonnegative-integer-array' ).primitives;
var isEqualArray = require( '@stdlib/assert/is-equal-array' );
var copy = require( '@stdlib/array/base/copy' );
var format = require( '@stdlib/string/format' );


// MAIN //

/**
* Sets the table cell left padding(s) in units of whitespace.
*
* @private
* @param {(Array<NonNegativeInteger>|NonNegativeInteger)} value - cell left padding(s)
* @throws {TypeError} must provide a nonnegative integer or an array of nonnegative integers
* @throws {RangeError} must provide the correct number of columns
*/
function set( value ) {
	if ( isNonNegativeInteger( value ) ) {
		value = [ value ];
		if ( !isEqualArray( value, this._paddingLeft ) ) {
			this._paddingLeft = value;
			if ( !this._quiet ) {
				this.emit( 'change' );
			}
		}
		return;
	}
	if ( !isNonNegativeIntegerArray( value ) ) {
		throw new TypeError( format( 'invalid assignment. `%s` must be a nonnegative integer or an array of nonnegative integers. Value: `%s`.', 'paddingLeft', value ) );
	}
	if ( this._numColumns ) {
		if ( value.length !== this._numColumns ) {
			throw new RangeError( format( 'invalid assignment. `%s` must have a length of %d in order to match the number of table columns. Value: `[%s]`.', 'paddingLeft', this._numColumns, copy( value ).join( ', ' ) ) );
		}
	} else {
		this._numColumns = value.length;
	}
	value = copy( value );
	if ( !isEqualArray( value, this._paddingLeft ) ) {
		this._paddingLeft = value;
		if ( !this._quiet ) {
			this.emit( 'change' );
		}
	}
}


// EXPORTS //

module.exports = set;
