/* eslint-disable no-invalid-this */

'use strict';

// MODULES //

var contains = require( '@stdlib/array/base/assert/contains' ).factory;
var format = require( '@stdlib/string/format' );


// VARIABLES //

var MODES = [ 'resume', 'interpolate', 'repeat' ];
var isMode = contains( MODES );


// MAIN //

/**
* Sets the vertical line separator mode.
*
* @private
* @param {string} value - line separator mode
* @throws {TypeError} must be a supported line separator mode
*/
function set( value ) {
	if ( !isMode( value ) ) {
		throw new TypeError( format( 'invalid assignment. `%s` must be one of the following: "%s". Value: `%s`.', 'verticalSeparatorMode', MODES.join( '", "' ), value ) );
	}
	if ( value !== this._verticalSeparatorMode ) {
		this._verticalSeparatorMode = value;
		if ( !this._quiet ) {
			this.emit( 'change' );
		}
	}
}


// EXPORTS //

module.exports = set;
