'use strict';

// MODULES //

var tape = require( 'tape' );
var ctor = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof ctor, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function adds a row to existing data', function test( t ) {
	var expected;
	var headers;
	var table;
	var data;
	var row;
	var v;

	data = [ [ 45, 33, 'hello' ], [ 32.54, true, null ] ];
	headers = [ 'col1', 'col2', 'col3' ];
	table = ctor( data, headers );

	row = [ 1, true, 'hello' ];
	table.addRow( row );
	v = table.getData();

	expected = {};
	expected.data = [ [ 45, 33, 'hello' ], [ 32.54, true, null ], [ 1, true, 'hello' ] ];
	expected.headers = [ 'col1', 'col2', 'col3' ];

	t.deepEqual( v, expected, 'sets table data' );

	t.end();
});

tape( 'the function creates table data if no existing data found', function test( t ) {
	var expected;
	var table;
	var row;
	var v;

	table = ctor();

	row = [ 1, true, 'hello' ];
	table.addRow( row );
	v = table.getData();

	expected = {};
	expected.data = [ [ 1, true, 'hello' ] ];
	expected.headers = null;

	t.deepEqual( v, expected, 'sets table data' );

	t.end();
});
