'use strict';

// MODULES //

var tape = require( 'tape' );
var ctor = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof ctor, 'function', 'main export is a function' );
	t.end();
});

tape( 'an instance throws an error if provided an invalid `borders` value', function test( t ) {
	var values;
	var i;

	values = [
		5,
		NaN,
		true,
		false,
		null,
		void 0,
		{},
		[],
		'hello',
		'1234567'
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			var table = ctor();
			table.borders = value;
		};
	}
});

tape( 'an instance supports setting and getting the property value', function test( t ) {
	var table;
	var data = null;

	table = ctor( data, {
		'borders': '1 2 3 4'
	});
	t.strictEqual( table.borders, '1 2 3 4', 'returns expected value' );

	table.borders = '- | _ |';
	t.strictEqual( table.borders, '- | _ |', 'returns expected value' );

	table.borders = 'None';
	t.strictEqual( table.borders, 'None', 'returns expected value' );

	t.end();
});
