'use strict';

// MODULES //

var tape = require( 'tape' );
var ndarray = require( '@stdlib/ndarray/ctor' );
var ctor = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof ctor, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function sets the table data for an array of objects (with headers)', function test( t ) {
	var expected;
	var headers;
	var table;
	var data;
	var v;

	data = [
		{
			'col1': 'hello',
			'col2': 45,
			'col3': true
		},
		{
			'col1': null,
			'col3': 45.3
		}
	];
	headers = [ 'col1', 'col2', 'col3' ];
	table = ctor();

	table.setData( data, headers );
	v = table.getData();

	expected = {};
	expected.data = [ [ 'hello', 45, true ], [ null, undefined, 45.3 ] ];
	expected.headers = [ 'col1', 'col2', 'col3' ];

	t.deepEqual( v, expected, 'sets table data' );

	t.end();
});

