/* eslint-disable no-underscore-dangle */

'use strict';

// MODULES //

var format = require( '@stdlib/string/format' );
var log = require( './../log.js' );


// MAIN //

/**
* Returns a callback to be invoked upon calling the `defaultTheme` command.
*
* @private
* @param {REPL} repl - REPL instance
* @returns {Function} callback
*/
function command( repl ) {
	return onCommand;

	/**
	* Gets (and sets) the default color theme for syntax-highlighting.
	*
	* @private
	* @param {string} [name] - theme name
	* @returns {void}
	*/
	function onCommand() {
		var theme;
		var nargs;
		var name;

		nargs = arguments.length;
		if ( nargs === 0 ) {
			theme = repl.getDefaultTheme();
			log( repl, format( 'Default theme: `%s`', theme ) );
			return;
		}
		name = arguments[ 0 ];
		try {
			repl.setDefaultTheme( name );
		} catch ( err ) {
			repl._ostream.write( format( 'Error: %s\n', err.message ) );
			return;
		}
		log( repl, format( '\nSuccessfully updated default theme to `%s`.', name ) );
	}
}


// EXPORTS //

module.exports = command;
