'use strict';

// MODULES //

var format = require( '@stdlib/string/format' );
var log = require( './../log.js' );


// MAIN //

/**
* Returns a callback to be invoked upon calling the `themes` command.
*
* @private
* @param {REPL} repl - REPL instance
* @returns {Function} callback
*/
function command( repl ) {
	return onCommand;

	/**
	* Gets available color themes for syntax-highlighting.
	*
	* @private
	* @returns {void}
	*/
	function onCommand() {
		var output;
		var themes;
		var i;

		themes = repl.themes();
		if ( themes.length === 0 ) {
			log( repl, 'No available themes.' );
		}
		output = format( 'Available themes: `%s`', themes[ 0 ] );
		for ( i = 1; i < themes.length; i++ ) {
			output += format( ', `%s`', themes[ i ] );
		}
		output += '.';
		log( repl, output );
	}
}


// EXPORTS //

module.exports = command;
